mapper.select:
select date_format(o.orderDate, '%Y-%m-%d') as period, sum(g.gdsPrice*d.cartStock)*0.00001 as totalPrice, count(o.orderId) as orderCnt, count(case when p.status like '취소%' or p.status like '반품%' or p.status like '교환%' then 1 end) as cancleCnt from ordertbl o, orderDetail d, goods g, orderProcess p where o.orderId = d.orderId and g.gdsCode = d.gdsCode and o.orderId = p.orderId
and date_format(o.orderDate, '%Y-%m-%d') >= #{startDate} and date_format(orderDate, '%Y-%m-%d') <= #{endDate}
group by date_format(orderDate, '%Y-%m-%d')
